insert into campeones(id_campeon,nombre,descripcion,dificultad,imagen,posicion,id_rol) values (1,'Aatrox','Aatrox y sus hermanos, otrora respetados defensores de Shurima contra el Vacío, acabarían convirtiéndose en una amenaza aún mayor para Runaterra y solo conocieron la derrota ante el uso astuto de hechizos mortales. No obstante, tras siglos de reclusión, Aatrox fue el primero en recuperar su libertad y se dedicó a corromper y transformar a cualquier necio que intentase empuñar el arma mágica que contenía su esencia. Ahora, envuelto en carne y huesos que no le pertenecen y que ha transformado en una horrenda aproximación de su antiguo aspecto, recorre Runaterra en busca de la apocalíptica venganza que tanto anhela.',2,'assets/Aatrox.jpg','Top',1)
insert into campeones(id_campeon,nombre,descripcion,dificultad,imagen,posicion,id_rol) values (2,'Ahri','Ahri es una vastaya conectada de forma innata al poder latente de Runaterra, y es capaz de convertir la magia en orbes de energía pura. Le gusta jugar con su presa, manipulando sus emociones antes de devorar su esencia de vida. A pesar de su naturaleza depredadora, Ahri posee cierto sentido de la empatía, ya que recibe recuerdos de cada alma que consume.',2,'assets/Ahri.jpg','Medio',3)
