insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (1,'ASPECTO DE LA MUERTE','De forma periódica, el siguiente ataque básico de Aatrox inflige daño físico adicional y cura a Aatrox en función de la vida máxima del objetivo.','Pasiva',1)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (2,'LA ESPADA DE LOS OSCUROS','Aatrox golpea con su espadón e inflige daño físico. Puede atacar tres veces, cada vez con un área de efecto distinta.','Q',1)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (3,'CADENAS INFERNALES','Aatrox golpea el suelo, lo que inflige daño al primer enemigo impactado. Los campeones y monstruos grandes deben abandonar el área de impacto rápidamente para evitar ser arrastrados hacia el centro y volver a recibir daño.','W',1)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (4,'DESLIZAMIENTO SOMBRÍO','Aatrox se cura de forma pasiva cuando inflige daño a campeones enemigos. Al activarse, se desliza en una dirección.','E',1)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (5,'EL ANIQUILADOR DE MUNDOS','Aatrox libera su forma demoníaca, lo que aterroriza a los súbditos enemigos cercanos, además de aumentar su daño de ataque, curación y velocidad de movimiento. Si consigue un asesinato o asistencia, la duración de este efecto se alarga.','R',1)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (6,'ROBO DE ESENCIAS','Cuando Ahri golpea a 9 enemigos con sus habilidades, su siguiente habilidad también la cura por cada enemigo alcanzado.','Pasiva',2)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (7,'ORBE DEL ENGAÑO','Ahri lanza y recupera su orbe, lo que inflige daño mágico al lanzarlo y daño verdadero al recuperarlo.','Q',2)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (8,'FUEGO ZORRUNO','Ahri obtiene una breve mejora de velocidad de movimiento y lanza tres fuegos zorrunos que fijan y atacan a los enemigos cercanos.','W',2)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (9,'HECHIZAR','Ahri lanza un beso que daña y hechiza al enemigo al que alcance primero, con lo que cancela instantáneamente todas sus habilidades de movimiento y hace que avance hacia ella de forma inofensiva. Ahri inflige daño adicional al objetivo de forma temporal.','E',2)
insert into habilidades(id_habilidad,nombre_habilidad,descripcion_habilidad,letra_habilidad,id_campeon) values (10,'IMPULSO ESPIRITUAL','Ahri se desliza hacia delante y libera rayos de esencia que infligen daño a los enemigos cercanos. Impulso espiritual puede usarse hasta tres veces antes de entrar en enfriamiento.','R',2)
